contract ExchangeDino is Ownable, Pausable {
	uint256 public minAmount = 1 * (10**18); //1 token with 18 decimals
	uint256 public maxAmount = 700000 * (10**18); //1000 tokens with 18 decimals
	uint256 public errorPercetage = 1;

	IERC20 public theToken = IERC20(0x49642110B712C1FD7261Bc074105E9E44676c68F); //the token being sold - DINO

	IUniswapV3Pool public pool = IUniswapV3Pool(0x19c10E1F20df3a8c2AC93a62d7FBA719fa777026);

	/**
      Events
    */
    event Withdraw(address indexed user, uint256 amount);
	event WithdrawTokens(address indexed user, IERC20 indexed token, uint256 amount);
    event TokenExchanged(address indexed user, uint256 amountTokens, uint256 amountEth);
	event AddFunds(address indexed user, uint256 amount);

	constructor() {
	}

	// add funds to the smart contract (must have approval)
	function addFunds(uint256 _amount) external {
		require(theToken.transferFrom(msg.sender, address(this), _amount), "Transfer failed");
		emit AddFunds(msg.sender, _amount);
	}

	//buy tokens for Wei.  _amountTokens is with decimals
	function buyWithWei(uint256 _amountTokens) external payable whenNotPaused {
		require(_amountTokens >= minAmount, "below min");
		require(_amountTokens <= maxAmount, "above max");
		uint amountOut = getAmountOutFromEth(msg.value);
		uint amountOfError = amountOut/1000*errorPercetage;
		require(amountOut + amountOfError > _amountTokens && amountOut - amountOfError < _amountTokens, "Expected amount is different more than expected");

		//you cannot buy more than the contract has
		uint256 balToken = theToken.balanceOf(address(this));
		require(_amountTokens <= balToken, "contract does not have enough balance");

		//finally give the user the tokens
		require(theToken.transfer(msg.sender, _amountTokens), "Transfer failed");
		emit TokenExchanged(msg.sender, _amountTokens, msg.value);
	}

	//sets the minimum tokens amount
	function setMinAmount(uint256 _newMinAmount) public onlyOwner {
		minAmount = _newMinAmount;
	}

	//sets the maximum tokens amount
	function setMaxAmount(uint256 _newMaxAmount) public onlyOwner {
		maxAmount = _newMaxAmount;
	}

	function pause() public onlyOwner {
		_pause();
	}

	function unpause() public onlyOwner {
		_unpause();
	}

	//owner can withdraw ETH
	function withdraw() public onlyOwner {
		uint256 ethBalance = address(this).balance;
		payable(msg.sender).transfer(ethBalance);
		emit Withdraw(msg.sender, ethBalance);
	}

	function withdrawTokens(IERC20 token, uint256 _amount) public onlyOwner {
		require(address(token) != address(0));
		uint256 balance = token.balanceOf(address(this));
		require(_amount <= balance, "not enough balance");
		require(token.transfer(msg.sender, _amount), "Transfer failed");
		emit WithdrawTokens(msg.sender, token, _amount);
	}

	function getSqrt() public view returns (uint256) {
        (uint160 sqrtPriceX96,,,,,,) =  pool.slot0();
         return sqrtPriceX96;
	}
    
	function getAmountOutFromEth(uint256 _amount) public view returns (uint256) {
		uint256 result =  (2**192)*_amount/(getSqrt()**2);
		return result;
	}

	function getAmountOutFromTokenToETH(uint256 _amount) public view returns (uint256) {
        uint256 result = ((getSqrt()**2)*_amount)/(2**192);
        return result;
	}

}
