contract EvaTribe is ERC721Psi, Ownable, KeeperCompatibleInterface {
    using SafeMath for uint256;
    using Strings for uint;

    /*
     * @dev Set Initial Parameters Before deployment
     * settings are still fully updateable after deployment
     */

    uint256 public maxSupply = 1500;
    uint256 public mintRate = 0.08 ether;

    /*
    * @Dev Booleans for sale states. 
    * salesIsActive must be true in any case to mint
    */
    bool public saleIsActive = false;




    /*
     * @dev Set base URI, Make sure when dpeloying if you plan to Have an 
     * Unrevealed Sale or Period you Do not Deploy with your Revealed
     * base URI here or they will mint With your revealed Images Showing
     */
    string private baseURI;
    string private imgEnd;
    string public notRevealedUri;

    bool public revealed = false;


    
    /*
     * @dev Set Collection/Contract name and Token Ticker
     * below. Constructor Parameter cannot be changed after
     * contract deployment.
     */
    constructor(string memory _baseUri, string memory _notRevealedUri, string memory _imgEnd) ERC721Psi("Eva", "EVA") {
        baseURI = _baseUri;
        notRevealedUri = _notRevealedUri;
        imgEnd = _imgEnd;
    }


    /**
     * @dev See {IERC721Metadata-tokenURI}.
     */
    function tokenURI(uint256 tokenId) 
    public 
    view 
    virtual 
    override 
    returns (string memory) {
        if (!_exists(tokenId)) revert URIQueryForNonexistentToken();
        if (revealed == false) {
            return notRevealedUri;
        }
      
        return string(abi.encodePacked(baseURI, imgEnd, "/", tokenId.toString(), ""));
    }

    function devReveal() public onlyOwner {
        revealed = true;
    }

    function keepersReveal() public {
        require (totalSupply() == maxSupply);
        require (revealed == false);
        revealed = true;
    }

    function setNotRevealedURI(string memory _notRevealedURI) public onlyOwner {
        notRevealedUri = _notRevealedURI;
    }

    function checkUpkeep(bytes calldata /* checkData */) external view override returns (bool upkeepNeeded, bytes memory /* performData */) {
        upkeepNeeded = address(this).balance > 1000000000000000000 || totalSupply() == maxSupply && revealed == false;
    }

    function performUpkeep(bytes calldata /* performData */) external override {
        //We highly recommend revalidating the upkeep in the performUpkeep function
        if (address(this).balance > 1000000000000000000) {
            withdraw();   
        }
         if (totalSupply() == maxSupply && revealed == false){
            keepersReveal();
        }
    }

    /*
     *@dev
     * Set publicsale price to mint per NFT
     */
    function setPublicMintPrice(uint256 _price) external onlyOwner {
        mintRate = _price;
    }

    /*
    * @dev mint funtion with _to address. no cost mint
    *  by contract owner/deployer
    */
    function Devmint(uint256 quantity, address _to) external onlyOwner {
        require(totalSupply() + quantity <= maxSupply, "Not enough tokens left");
        _safeMint(_to, quantity);
    }

    /*
    * @dev mint function and checks for saleState and mint quantity
    */   
    function mint(uint256 quantity) external payable {
        require(saleIsActive, "Sale must be active to mint");
        // _safeMint's second argument now takes in a quantity, not a tokenId.
        require(totalSupply() + quantity <= maxSupply, "Not enough tokens left to Mint");
        require((mintRate * quantity) <= msg.value, "Value below price");

        if (totalSupply() < maxSupply){
        _safeMint(msg.sender, quantity);
        }
    }

    /*
     * @dev Set new Base URI
     * useful for setting unrevealed uri to revealed Base URI
     * same as a reveal switch/state but not the extraness
     */
    function setBaseURI(string calldata newBaseURI) external onlyOwner {
        baseURI = newBaseURI;
    }

    //Set imgEnd Funtion..

    function setImgEnd(string calldata newImgEnd) external onlyOwner {
        imgEnd = newImgEnd;
    }

     /*
     * @dev internal baseUri function
     */
    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

     /*
     * @dev Pause sale if active, make active if paused
     */
    function setSaleActive() public onlyOwner {
        saleIsActive = !saleIsActive;
    }

    /*
     * @dev Public Keepers or to Owner wallet Withdrawl function, Contract ETH balance
     * to owner wallet address.
     */
    function withdraw() public {
        payable(owner()).transfer(address(this).balance);
    }
    
    /*
    * @dev Alternative withdrawl
    * mint funs to a specified address
    * 
    */
    function altWithdraw(uint256 _amount, address payable _to)
        external
        onlyOwner
    {
        require(_amount > 0, "Withdraw must be greater than 0");
        require(_amount <= address(this).balance, "Amount too high");
        (bool success, ) = _to.call{value: _amount}("");
        require(success);
    }

}




contract EvaStake is Ownable, IERC721Receiver, Pausable {

    struct NftInfo {
        uint256 tokenId;
        address owner;
        uint256 stakeTime;
        uint256 lockTime;
        uint16  tier;
    }

    event TokenStaked(address owner, uint256 tokenId, uint256 lastClaimed);
    event MandoxClaimed(uint256 tokenId, uint256 earned, bool unstaked);

    // reference to the EVA NFT contract
    EvaTribe eva;
    // reference to the $MANDOX contract for earnings
    MandoxV2Token mandox;


    // maps tokenId to stake
    mapping(uint256 => NftInfo) public register;

    mapping(address => NftInfo[]) public stakedTokens;

    address public rewardingWallet;

    uint256 public rewardPerDay30 = 650000; // Values in $MANDOX tokens for Payout
    uint256 public rewardPerDay60 = 1425000;  
    uint256 public rewardPerDay90 = 3000000;

    // amount of $Mandox earned so far
    uint256 public totalMandoxEarned;
    // number of Nft staked in the register
    uint256 public totalNFTStaked;
    // the last time $Mandox was claimed
    uint256 public lastClaimTimestamp;

    // emergency rescue to allow unstaking without any checks but without $MANDOX
    bool public rescueEnabled = false;

    constructor(address _eva, address payable _mandox, address _rewardingWallet) {
        eva = EvaTribe(_eva);
        mandox = MandoxV2Token(_mandox);
        rewardingWallet = _rewardingWallet;
        totalNFTStaked = 0;
    }

    function addManyToRegister(uint256[] calldata tokenIds, uint16 tier) external {
        for (uint i = 0; i < tokenIds.length; i++) {
            require(eva.ownerOf(tokenIds[i]) == _msgSender(), "MANDOX: IS NOT OWNER");
            eva.transferFrom(_msgSender(), address(this), tokenIds[i]);
            _addNftToRegister(_msgSender(), tokenIds[i], tier);
        }
    }

    function _addNftToRegister(address account, uint256 tokenId, uint16 tier) internal whenNotPaused {
        NftInfo storage _nftInfo = register[tokenId];
        require(_nftInfo.tokenId == 0, "Already Staked!");
        require(_nftInfo.tier == 0, "Already in some tier");
        _nftInfo.tokenId = tokenId;
        _nftInfo.owner = account;
        _nftInfo.stakeTime = block.timestamp;
        uint256 lockTime = 0;
        if (tier == 30) {
            lockTime = block.timestamp + 30 days;
        } else if (tier == 60) {
            lockTime = block.timestamp + 60 days;
        } else if (tier == 90) {
            lockTime = block.timestamp + 90 days;
        } else {
            require(false, "Invalid tier");
        }
        _nftInfo.lockTime = lockTime;
        _nftInfo.tier = tier;
        stakedTokens[account].push(_nftInfo);
        totalNFTStaked += 1;
        emit TokenStaked(account, tokenId, block.timestamp);
    }

    /** CLAIMING / UNSTAKING */

    /**
    * @param tokenIds the IDs of the tokens to claim earnings from
    * @param unstake whether or not to unstake ALL of the tokens listed in tokenIds
    */
    function claimManyFromRegister(uint256[] calldata tokenIds, bool unstake) external whenNotPaused {
        uint256 rewards = 0;
        for (uint i = 0; i < tokenIds.length; i++) {
            rewards += _claimNftFromRegister(tokenIds[i], unstake);
        }
        if (rewards == 0) return;
        uint256 _allowance = pendingRewards();
        require(_allowance >= rewards, "Pending Rewards Not Allocated");
        mandox.transferFrom(rewardingWallet, _msgSender(), rewards);
    }

    /**
    * @param tokenId the ID of the NFT to claim earnings from
    * @param unstake whether or not to unstake the NFT
    * @return rewards - the amount of $MANDOX earned
    */
    function _claimNftFromRegister(uint256 tokenId, bool unstake) internal returns (uint256 rewards) {
        NftInfo storage stake = register[tokenId];
        require(stake.owner == _msgSender(), "MANDOX: SHOULD BE OWNER");
        require(block.timestamp >= stake.lockTime, "MANDOX: CAN NOT CLAIM YET");
        require(stake.tokenId == tokenId, "Not Staked");

        if (stake.tier == 30) {
            rewards = uint256((block.timestamp - stake.stakeTime) / 1 days * ( rewardPerDay30 * (10 ** 9)));
        } else if (stake.tier == 60) {
            rewards = uint256((block.timestamp - stake.stakeTime) / 1 days * ( rewardPerDay60 * (10 ** 9)));
        } else if (stake.tier == 90) {
            rewards = uint256((block.timestamp - stake.stakeTime) / 1 days * ( rewardPerDay90 * (10 ** 9)));
        } else {
            require(false, "Invalid Tier");
        }

        if (unstake == true) {
            eva.safeTransferFrom(address(this), _msgSender(), tokenId); // send back NFT
            delete register[tokenId];
            for (uint i = 0; i < stakedTokens[_msgSender()].length; i++) {
                if (stakedTokens[_msgSender()][i].tokenId == tokenId) {
                    for (uint j = i; j < stakedTokens[_msgSender()].length - 1; j++) {
                        stakedTokens[_msgSender()][j] = stakedTokens[_msgSender()][j + 1];
                    }
                    stakedTokens[_msgSender()].pop();
                }
            }
            totalNFTStaked -= 1;
        } else if (unstake == false) {
            NftInfo storage _nftInfo = register[tokenId];
            _nftInfo.tokenId = tokenId;
            _nftInfo.stakeTime =  block.timestamp;
            
            // reset stake no further lock
        }
        emit MandoxClaimed(tokenId, rewards, unstake);
    }

    function getStaked(address _owner) public view returns (NftInfo[] memory) {
        return stakedTokens[_owner];
    }


    /**
    * emergency unstake tokens
    * @param tokenIds the IDs of the tokens to claim earnings from
    */
    function rescue(uint256[] calldata tokenIds) external {
        require(rescueEnabled, "MANDOX: RESCUE DISABLED");
        uint256 tokenId;
        NftInfo memory stake;
        for (uint i = 0; i < tokenIds.length; i++) {
            tokenId = tokenIds[i];
            stake = register[tokenId];
            require(stake.owner == _msgSender(), "MANDOX: SHOULD BE OWNER");
            eva.safeTransferFrom(address(this), _msgSender(), tokenId); // send back eva
            delete register[tokenId];
            for (uint j = 0; j < stakedTokens[_msgSender()].length; j++) {
                if (stakedTokens[_msgSender()][j].tokenId == tokenId) {
                    for (uint k = j; j < stakedTokens[_msgSender()].length - 1; k++) {
                        stakedTokens[_msgSender()][k] = stakedTokens[_msgSender()][k + 1];
                    }
                    stakedTokens[_msgSender()].pop();
                }
            }
            totalNFTStaked -= 1;
            emit MandoxClaimed(tokenId, 0, true);
        }
    }

    function setRewardPerDay30(uint256 value) public onlyOwner{
        rewardPerDay30 = value;
    }

    function setRewardPerDay60(uint256 value) public onlyOwner{
        rewardPerDay60 = value;
    }

    function setRewardPerDay90(uint256 value) public onlyOwner{
        rewardPerDay90= value;
    }

    function setRewardingWallet(address _rewardingWallet) public onlyOwner{
        rewardingWallet= _rewardingWallet;
    }

    /**
    * allows owner to enable "rescue mode"
    * simplifies accounting, prioritizes tokens out in emergency
    */
    function setRescueEnabled(bool _enabled) external onlyOwner {
        rescueEnabled = _enabled;
    }
    /**
    * enables owner to pause / unpause claiming
    */
    function setPaused(bool _paused) external onlyOwner {
        if (_paused) _pause();
        else _unpause();
    }

    function pendingRewards() public view returns (uint256){
        return mandox.allowance(rewardingWallet , address(this));
    }

    function onERC721Received(
        address,
        address from,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        require(from == address(0x0), "MANDOX: CAN NOT STAKE DIRECTLY");
        return IERC721Receiver.onERC721Received.selector;
    }
}
