// File contracts/dori-by-dream.sol


pragma solidity >=0.8.9 <0.9.0;





contract DoriByDream  is ERC721A, Ownable, ReentrancyGuard, DefaultOperatorFilterer {
    using Strings for uint256;

    mapping (address => uint256) public walletTokens;
    uint256 public price = 0 ether;
    uint256 public maxTokensPerTx = 1;
    uint256 public freeTokensPerWallet = 1;
    uint256 public limitTokensPerWallet = 1;
    uint256 public maxSupply = 222;
    bool public openForMint = true;
    string public baseURI = "https://purple-electoral-penguin-724.mypinata.cloud/ipfs/QmQJmJSJQxMa58TvUaqsCELjUjf1KDhFdKFyfP67y53j3J/";


    constructor() ERC721A("Dori By Dream", "DORAI")  DefaultOperatorFilterer(){}

    function mint(uint256 amount) external payable {
        require(amount > 0, "Invalid mint amount!");
        require(totalSupply() + amount <= maxSupply, "Max supply exceeded!");
        if (msg.sender != owner()) {
            require(openForMint, "Public mint is not open yet");
            require(amount <= maxTokensPerTx, "Exceeded max number tokens per tx");
            require(walletTokens[msg.sender] + amount <= limitTokensPerWallet, 'Exceed limit tokens per wallet!');            
            require(tx.origin == msg.sender, "The caller is another contract");
            uint256 tokensAlreadyMint = walletTokens[msg.sender];
            uint256 remainFree = (freeTokensPerWallet > tokensAlreadyMint ? (freeTokensPerWallet - tokensAlreadyMint) : 0);
            require(
                msg.value >= (amount - remainFree) * price,
                "Insufficient funds!"
            );
            walletTokens[msg.sender] += amount;
        }
        _mint(msg.sender, amount);
    }

    function devMint(uint256 _mintAmount, address _receiver) public onlyOwner {
        require(
            totalSupply() + _mintAmount <= maxSupply,
            "Max supply exceeded!"
        );
        _mint(_receiver, _mintAmount);
    }

    function _startTokenId() internal view virtual override returns (uint256) {
        return 1;
    }

    function setMaxTokensPerTx(uint256 _maxTokensPerTx) external onlyOwner {
        maxTokensPerTx = _maxTokensPerTx;
    }

    function setMaxTokensPerWallet(uint256 _maxTokensPerWallet) external onlyOwner {
        limitTokensPerWallet = _maxTokensPerWallet;
    }

    function setTokenPrice(uint256 _price) public onlyOwner {
        price = _price;
    }

    function setFreeTokensPerWallet(uint256 amount) public onlyOwner {
        freeTokensPerWallet = amount;
    }

    function tokenURI(uint256 _tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        require(
            _exists(_tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );

        string memory currentBaseURI = baseURI;
        return
            bytes(currentBaseURI).length > 0
                ? string(
                    abi.encodePacked(
                        currentBaseURI,
                        _tokenId.toString(),
                        ".json"
                    )
                )
                : "";
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI = _newBaseURI;
    }

    function setMaxSupply(uint256 _maxSupply) external onlyOwner {
        maxSupply = _maxSupply;
    }

    function setSaleForPublic(bool _state) public onlyOwner {
        openForMint = _state;
    }

    function withdraw() public onlyOwner nonReentrant {
        (bool os, ) = payable(owner()).call{value: address(this).balance}("");
        require(os);
    }

    function transferFrom(address from, address to, uint256 tokenId)
        public
        override
        onlyAllowedOperator(from)
    {
        super.transferFrom(from, to, tokenId);
    }

    function safeTransferFrom(address from, address to, uint256 tokenId)
        public
        override
        onlyAllowedOperator(from)
    {
        super.safeTransferFrom(from, to, tokenId);
    }

    function safeTransferFrom(address from, address to, uint256 tokenId, bytes memory data)
        public
        override
        onlyAllowedOperator(from)
    {
        super.safeTransferFrom(from, to, tokenId, data);
    }
}
