pragma solidity 0.8.17;
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
contract Claimer is Ownable, ReentrancyGuard
{
    /*-------------------*/
    /*      STRUCT       */
    /*-------------------*/

    struct User
    {
        bool[] RegularClaim;
        bool[] CitizenClaim;
    }

    /*-------------------*/
    /*  STATE VARIABLES  */
    /*-------------------*/

    uint private constant _ClaimCost = 0.0042 ether;
    address private constant _BRT_BURN = 0xcff43A597911a9457071d89d2b2AC3D5b1862b86;
    address private constant _GTMX = 0xE0D1Fa3fBd72db2eD179F80C0459B7dA93Fe4FE8;
    address private constant _CITIZENS = 0xbDdE08BD57e5C9fD563eE7aC61618CB2ECdc0ce0;
    address private constant _MOMENT = 0x0A1BBD57033F57E7B6743621b79fCB9Eb2CE3676;
    address private constant _BRT_MULTISIG = 0x93F01412C062C99C6ef105b1BAd93800B5635479;
    bytes32 public _RootCaminos = 0x78e8deb95256b6541d8ed458a6b552586c8e0a1285de694de8f66b95f70bfc35;
    bytes32 public _RootCitizens = 0x597ce7032f97f6a6c73163eceff668e14dd5b046801cf114bddfc136105051b9;

    /*-------------------*/
    /*     MAPPINGS      */
    /*-------------------*/

    mapping(uint=>bool) public ClaimedCaminos;
    mapping(uint=>bool) public ClaimedCitizens;

    /*-------------------*/
    /*      EVENTS       */
    /*-------------------*/

    event TokensClaimed(address Redeemer, uint[] TokenIDs);
    event CitizensClaimed(address Redeemer, uint[] CitizenTokenIDs, uint[] GTMXTokenIDs);

    /*-------------------*/
    /*    CONSTRUCTOR    */
    /*-------------------*/

    constructor() { _transferOwnership(0xe06F5FAE754e81Bc050215fF89B03d9e9FF20700); } // `operator.brightmoments.eth`

    /*----------------------*/
    /*  EXTERNAL FUNCTIONS  */
    /*----------------------*/

    /**
     * @dev Claims TokenID(s) With Merkle
     */
    function MerkleClaim (
        uint[] calldata TokenIDs,
        bytes32[][] calldata Proof 
    ) external payable nonReentrant {
        require(Proof.length > 0 && TokenIDs.length > 0, "Claimer: Invalid Input");
        require(Proof.length == TokenIDs.length, "Claimer: Arrays Must Match");
        require(msg.value == _ClaimCost * TokenIDs.length, "Claimer: Invalid Message Value Sent");
        bool[] memory Eligibles = ReadEligibilityMerkleCaminos(msg.sender, TokenIDs, Proof);
        for(uint Index; Index < TokenIDs.length; Index++)
        {
            require(Eligibles[Index], "Claimer: Invalid Merkle");
            require(!ClaimedCaminos[TokenIDs[Index]], "Claimer: Caminos Already Claimed");
            ClaimedCaminos[TokenIDs[Index]] = true;
            IERC721(_MOMENT).transferFrom(_BRT_MULTISIG, msg.sender, TokenIDs[Index]);
        }
        emit TokensClaimed(msg.sender, TokenIDs);
    }

    /**
     * @dev Claims TokenID(s) With Merkle
     */
    function MerkleClaimCitizens (
        uint[] calldata GTMXTokenIDs,
        uint[] calldata CitizenTokenIDs,
        bytes32[][] calldata Proof 
    ) external nonReentrant {
        require(Proof.length > 0 && GTMXTokenIDs.length > 0 && CitizenTokenIDs.length > 0, "Claimer: Invalid Input");
        require(GTMXTokenIDs.length == CitizenTokenIDs.length && CitizenTokenIDs.length == Proof.length, "Claimer: Arrays Must Match");
        bool[] memory Eligibles = ReadEligibilityMerkleCitizens(GTMXTokenIDs, CitizenTokenIDs, Proof);
        for(uint Index; Index < GTMXTokenIDs.length; Index++)
        {
            require(IERC721(_GTMX).ownerOf(GTMXTokenIDs[Index]) == msg.sender, "User Does Not Hold GTMX");
            IERC721(_GTMX).transferFrom(msg.sender, _BRT_BURN, GTMXTokenIDs[Index]);
            require(Eligibles[Index], "Claimer: Invalid Merkle");
            require(!ClaimedCitizens[CitizenTokenIDs[Index]], "Claimer: Citizen Already Claimed");
            ClaimedCitizens[CitizenTokenIDs[Index]] = true;
            IERC721(_CITIZENS).transferFrom(_BRT_MULTISIG, msg.sender, CitizenTokenIDs[Index]);
        }
        emit CitizensClaimed(msg.sender, CitizenTokenIDs, GTMXTokenIDs);
    }

    /*-------------------*/
    /*  OWNER FUNCTIONS  */
    /*-------------------*/

    /**
     * @dev Changes Citizen Root
     */
    function __ChangeCitizenRoot(bytes32 NewRoot) external onlyOwner { _RootCitizens = NewRoot; }

    /**
     * @dev Changes Caminos Root
     */
    function __ChangeCaminosRoot(bytes32 NewRoot) external onlyOwner { _RootCaminos = NewRoot; }

    /**
     * @dev Withdraws All Ether From The Contract
     */
    function ___WithdrawEther() external onlyOwner { payable(msg.sender).transfer(address(this).balance); }

    /**
     * @dev Withdraws Ether From Contract To Address With An Amount
     */
    function ___WithdrawEtherToAddress(address payable Recipient, uint Amount) external onlyOwner
    {
        require(Amount > 0 && Amount <= address(this).balance, "Claimer: Invalid Amount");
        (bool Success, ) = Recipient.call{value: Amount}("");
        require(Success, "Claimer: Unable to Withdraw, Recipient May Have Reverted");
    }

    /*------------------*/
    /*  VIEW FUNCTIONS  */
    /*------------------*/

    /**
     * @dev Returns A Wallet's Merkle Eligibility
     */
    function ReadEligibilityMerkleCaminos (
        address Wallet,
        uint[] calldata TokenIDs,
        bytes32[][] calldata Proof
    ) public view returns (bool[] memory) {
        bool[] memory Eligibles = new bool[](TokenIDs.length);
        for(uint Index; Index < TokenIDs.length; Index++)
        {
            bytes32 Leaf = keccak256(abi.encodePacked(Wallet, TokenIDs[Index]));
            if(!ClaimedCaminos[TokenIDs[Index]]) { Eligibles[Index] = MerkleProof.verify(Proof[Index], _RootCaminos, Leaf); }
        }
        return Eligibles;
    }

    /**
     * @dev Returns A Wallet's Merkle Eligibility
     */
    function ReadEligibilityMerkleCitizens (
        uint[] calldata GTMXTokenIDs, 
        uint[] calldata CitizenTokenIDs,
        bytes32[][] calldata Proof
    ) public view returns (bool[] memory) {
        bool[] memory Eligibles = new bool[](GTMXTokenIDs.length);
        for(uint Index; Index < GTMXTokenIDs.length; Index++)
        {
            bytes32 Leaf = keccak256(abi.encodePacked(GTMXTokenIDs[Index], CitizenTokenIDs[Index]));
            if(!ClaimedCitizens[CitizenTokenIDs[Index]]) { Eligibles[Index] = MerkleProof.verify(Proof[Index], _RootCitizens, Leaf); }
        }
        return Eligibles;
    }

    /**
     * @dev Returns Merkle Eligibilities
     */
    function ReadEligibility (
        address Wallet,
        uint[] calldata GTMXTokenIDs,
        uint[] calldata CitizenTokenIDs,
        uint[] calldata CaminosTokenIDs,
        bytes32[][] calldata CitizenProof,
        bytes32[][] calldata CaminosProof
    ) public view returns (User memory) {
        return User (
            ReadEligibilityMerkleCaminos(Wallet, CaminosTokenIDs, CaminosProof),
            ReadEligibilityMerkleCitizens(GTMXTokenIDs, CitizenTokenIDs, CitizenProof)
        );
    }
}"
    }
