        for (uint256 i = 0; i < addresses.length; i++) {
            allowedMinters[addresses[i]] = allowances[i];
        }
    }

    // WITHDRAWALS //

    // @notice: Withdraw any Eth in this contract to the Mona Wallet.
    function withdrawEth() public onlyOwner {
        (bool success, ) = payable(monaWallet).call{
            value: address(this).balance
        }("");
        require(success);
    }

    // @notice: Withdraw any ERC20 tokens in this contract to the Mona Wallet.
    // @param: amount: The amount of tokens to withdraw.
    // @param: token: The ERC20 token to withdraw.
    function withdrawERC20(uint256 amount, address token) public onlyOwner {
        bool success = IERC20(token).transfer(monaWallet, amount);
        require(success);
    }
}
