// File: my/aggregate/v3/AggregateV3.sol



pragma solidity ^0.8.4;












contract AggregateV3 is ReentrancyGuard,Ownable {
    address public feeAddress;
    uint public feeRate;
    IRouter public router;
    uint160 internal constant MIN_SQRT_RATIO = 4295128739;
    uint160 internal constant MAX_SQRT_RATIO = 1461446703485210103287273052203988822378723970342;
    ExactOutputCache internal cache;

    using Path for bytes;
    using SafeCast for uint256;

    event Fee(address indexed _feeAddress,address coin,uint amount);
    event AmountIn(address indexed user,address coin,uint amount);
    event AmountOut(address indexed user,address coin,uint amount);

    constructor(address _feeAddress,uint _feeRate) {
        feeAddress=_feeAddress;
        feeRate=_feeRate;
    }

    function feeAddressAdmin(address _feeAddress) external onlyOwner {
        feeAddress=_feeAddress;
    }

    function feeRateAdmin(uint _feeRate) external onlyOwner {
        feeRate=_feeRate;
    }

    modifier RouterSet(address _router) {
        router=IRouter(_router);
        _;
        router=IRouter(address(0));
    }

    function swapTokensForExactTokens(address coin0,uint24 fee0,address coin1,uint24 fee1,address coin2,uint24 fee2,address coin3,
                                      address route,
                                      uint amountInMax,uint amountOut,
                                      address to,uint deadline) external nonReentrant RouterSet(route) payable returns(uint amountIn) {
        require(block.timestamp <= deadline, "EXPIRED");
        cache.coin0=coin0;cache.fee0=fee0;cache.coin1=coin1;cache.fee1=fee1;cache.coin2=coin2;cache.fee2=fee2;cache.coin3=coin3;
        cache.from=msg.sender;
        amountIn=rightToMiddle(amountOut,to);
        require(cache.amountInCached<=amountInMax,"EXCESSIVE_INPUT_AMOUNT");
        emit AmountOut(to,coin3,amountOut);
        emit AmountIn(msg.sender,coin0,cache.amountInCached);
        delete cache;
    }


    function rightToMiddle(uint amountOut,address to) internal returns(uint amountIn) {
        amountIn=amountOut;
        address input=cache.coin2;
        uint24 fee=cache.fee2;
        address output=cache.coin3;
        cache.status=3;
        if(input==output&&input!=address(0)) {
            exactOutputFee(amountIn,to);
            return amountIn;
        }
        address weth=router.WETH9();
        address wapperInput=(input==address(0)?weth:input);
        address wapperOutput=(output==address(0)?weth:output);
        if(wapperInput==wapperOutput) {
            exactOutputFee(amountIn,address(this));
            if(output==address(0)) {
                IWETH(weth).withdraw(amountIn);
                payable(feeAddress).transfer(amountIn);
            } else {
                pay(output,address(this),to,amountIn);
            }
        } else {
            amountIn=exactOutputInternal(amountOut,to,0,
                SwapCallbackData({path: abi.encodePacked(wapperOutput, fee, wapperInput), payer: address(this)}));
        }

        uint wethBalance=balance(weth,address(this));
        if(wethBalance>0)
            IWETH(weth).withdraw(wethBalance);
        wethBalance=balance(address(0),address(this));
        if(wethBalance>0)
            payable(msg.sender).transfer(wethBalance);
    }

    function exactOutputFee(uint amountOut,address to) internal returns (uint amountIn) {
        amountIn=amountOut;
        if(feeAddress!=address(0)&&feeRate>0) {
            amountIn=amountOut*10000/(10000-feeRate);
        }
        middleToMiddle(amountIn,address(this));
        uint fee=amountIn-amountOut;
        address weth=router.WETH9();
        address coin=cache.coin2;
        address wapperCoin=(coin==address(0)?weth:coin);
        if(fee>0) {
            if(coin==address(0)) {
                IWETH(weth).withdraw(fee);
                payable(feeAddress).transfer(fee);
            } else {
                pay(cache.coin2,address(this),feeAddress,fee);
            }
            emit Fee(feeAddress,coin,fee);
        }
        pay(wapperCoin,address(this),to,amountOut);
    }

    function middleToMiddle(uint amountOut,address to) internal returns(uint amountIn) {
        amountIn=amountOut;
        address input=cache.coin1;
        uint24 fee=cache.fee1;
        address output=cache.coin2;
        cache.status=2;
        if(input==output&&input!=address(0)) {
            middleToLeft(amountIn,to);
            return amountIn;
        }
        address weth=router.WETH9();
        address wapperInput=(input==address(0)?weth:input);
        address wapperOutput=(output==address(0)?weth:output);
        if(wapperInput==wapperOutput) {
            middleToLeft(amountIn,to);
            return amountIn;
        } else {
            amountIn=exactOutputInternal(amountOut,to,0,
                SwapCallbackData({path: abi.encodePacked(wapperOutput, fee, wapperInput), payer: address(this)}));
        }
    }

    function middleToLeft(uint amountOut,address to) internal returns(uint amountIn) {
        amountIn=amountOut;
        address input=cache.coin0;
        uint24 fee=cache.fee0;
        address output=cache.coin1;
        cache.status=1;
        if(input==output&&input!=address(0)) {
            pay(input,cache.from,to,amountIn);
            cache.amountInCached=amountIn;
            return amountIn;
        }
        address weth=router.WETH9();
        address wapperInput=(input==address(0)?weth:input);
        address wapperOutput=(output==address(0)?weth:output);
        if(wapperInput==wapperOutput) {
            if(input==address(0)) {
                require(balance(address(0),address(this))>=amountIn,"insufficient eth");
                // if(msg.value>amountIn) {
                    // pay(input,address(this),cache.from,msg.value-amountIn);
                // }
                IWETH(weth).deposit{value: amountIn}();
                pay(weth,address(this),to,amountIn);
            }
            else{
                TransferLib.transferFrom(input,cache.from,to,amountIn);
            }
        } else {
            if(input==address(0)) {
                IWETH(weth).deposit{value: balance(address(0),address(this))}();
                amountIn=exactOutputInternal(amountOut,to,0,
                SwapCallbackData({path: abi.encodePacked(wapperOutput, fee, wapperInput), payer: address(this)}));
                //uint returnBalance=IWETH(weth).balanceOf(address(this));
                //if(returnBalance>0) {
                //    IWETH(weth).withdraw(returnBalance);
                //    payable(msg.sender).transfer(returnBalance);
                //}
            } else {
                amountIn=exactOutputInternal(amountOut,to,0,
                SwapCallbackData({path: abi.encodePacked(wapperOutput, fee, wapperInput), payer: cache.from}));
            }
        }
        cache.amountInCached=amountIn;
    }


    function exactOutputInternal(
        uint256 amountOut,
        address recipient,
        uint160 sqrtPriceLimitX96,
        SwapCallbackData memory data
    ) private returns (uint256 amountIn) {
        // allow swapping to the router address with address 0
        if (recipient == address(0)) recipient = address(this);

        (address tokenOut, address tokenIn, uint24 fee) = data.path.decodeFirstPool();

        bool zeroForOne = tokenIn < tokenOut;

        (int256 amount0Delta, int256 amount1Delta) =
        router.factory().getPool(tokenIn, tokenOut, fee).swap(
            recipient,
            zeroForOne,
            -amountOut.toInt256(),
            sqrtPriceLimitX96 == 0
            ? (zeroForOne ? MIN_SQRT_RATIO + 1 : MAX_SQRT_RATIO - 1)
            : sqrtPriceLimitX96,
            abi.encode(data)
        );

        uint256 amountOutReceived;
        (amountIn, amountOutReceived) = zeroForOne
        ? (uint256(amount0Delta), uint256(-amount1Delta))
        : (uint256(amount1Delta), uint256(-amount0Delta));
        // it's technically possible to not receive the full output amount,
        // so if no price limit has been specified, require this possibility away
        if (sqrtPriceLimitX96 == 0) require(amountOutReceived == amountOut);
    }

    function swapExactTokensForTokens(address coin0,uint24 fee0,address coin1,uint24 fee1,address coin2,uint24 fee2,address coin3,
                                      address route,
                                      uint amountIn,uint amountMinOut,
                                      address to,uint deadline) external nonReentrant RouterSet(route) payable returns(uint) {
        require(block.timestamp <= deadline, "EXPIRED");
        emit AmountIn(msg.sender,coin0,amountIn);
        uint amountOut=leftToMiddle(coin0,fee0,coin1,amountIn);
        amountOut=middleToMiddle(coin1,fee1,coin2,amountOut);
        amountOut=getFee(coin2);
        amountOut=middleToRight(coin2,fee2,coin3,amountOut,to);
        require(amountOut>=amountMinOut,"INSUFFICIENT_OUTPUT_AMOUNT");
        emit AmountOut(to,coin3,amountOut);
        return amountOut;
    }

    function middleToRight(address input,uint24 fee,address output,uint amount,address to) internal returns(uint amountOut) {
        amountOut=amount;
        if(input==output) {
            if(input!=address(0)) {
                pay(input,address(this),to,amount);
            } else {
                payable(to).transfer(amount);
            }
            return amount;
        }
        address weth=router.WETH9();
        address wapperInput=(input==address(0)?weth:input);
        address wapperOutput=(output==address(0)?weth:output);
        if(input==address(0)) {
            IWETH(weth).deposit{value: amount}();
        }
        if(wapperInput!=wapperOutput){
            if(output!=address(0)) {
                amountOut=exactInputInternal(amount,to,0,
                SwapCallbackData({path: abi.encodePacked(wapperInput, fee, wapperOutput), payer: address(this)}));
            } else {
                amountOut=exactInputInternal(amount,address(this),0,
                SwapCallbackData({path: abi.encodePacked(wapperInput, fee, wapperOutput), payer: address(this)}));
            }
        }
        if(output==address(0)) {
            IWETH(weth).withdraw(IWETH(weth).balanceOf(address(this)));
        }
        uint wethBalance=IWETH(weth).balanceOf(address(this));
        if(wethBalance>0) {
            pay(weth,address(this),to,wethBalance);
        }
        if(address(this).balance>0) {
            payable(to).transfer(address(this).balance);
        }
    }

    function middleToMiddle(address input,uint24 fee,address output,uint amount) internal returns(uint amountOut) {
        amountOut=amount;
        if(input==output) {
            return amount;
        }
        address weth=router.WETH9();
        address wapperInput=(input==address(0)?weth:input);
        address wapperOutput=(output==address(0)?weth:output);
        if(input==address(0)) {
            IWETH(weth).deposit{value: address(this).balance}();
        }
        if(wapperInput!=wapperOutput){
            amountOut=exactInputInternal(amount,address(this),0,
                SwapCallbackData({path: abi.encodePacked(wapperInput, fee, wapperOutput), payer: address(this)}));
        }
        if(output==address(0)) {
            IWETH(weth).withdraw(IWETH(weth).balanceOf(address(this)));
        }
    }

    function leftToMiddle(address input,uint24 fee,address output,uint amount) internal returns(uint amountOut) {
        amountOut=amount;
        if(input==output) {
            if(input!=address(0)){
                TransferLib.transferFrom(input,msg.sender,address(this),amount);
            } else {
                require(address(this).balance>=amount,"insufficient eth");
            }
            return amount;
        }
        address weth=router.WETH9();
        address wapperInput=(input==address(0)?weth:input);
        address wapperOutput=(output==address(0)?weth:output);
        if(input==address(0)) {
            require(address(this).balance>=amount,"insufficient eth");
            IWETH(weth).deposit{value: amount}();
        }
        if(wapperInput!=wapperOutput){
            if(input==address(0)) {
                amountOut=exactInputInternal(amount,address(this),0,
                SwapCallbackData({path: abi.encodePacked(wapperInput, fee, wapperOutput), payer: address(this)}));
            }else {
                amountOut=exactInputInternal(amount,address(this),0,
                SwapCallbackData({path: abi.encodePacked(wapperInput, fee, wapperOutput), payer: msg.sender}));
            }
        } else {
            if(input!=address(0)) {
                TransferLib.transferFrom(input,msg.sender,address(this),amount);
            }
        }
        if(output==address(0)) {
            IWETH(weth).withdraw(IWETH(weth).balanceOf(address(this)));
        }
    }

    function uniswapV3SwapCallback(
        int256 amount0Delta,
        int256 amount1Delta,
        bytes calldata _data
    ) external {
        require(amount0Delta > 0 || amount1Delta > 0); // swaps entirely within 0-liquidity regions are not supported
        SwapCallbackData memory data = abi.decode(_data, (SwapCallbackData));
        (address tokenIn, address tokenOut, uint24 fee) = data.path.decodeFirstPool();
        //CallbackValidation.verifyCallback(router.factory(), tokenIn, tokenOut, fee);
        require(msg.sender==address(router.factory().getPool(tokenIn, tokenOut, fee)),"not pool");

        (bool isExactInput, uint256 amountToPay) =
            amount0Delta > 0
                ? (tokenIn < tokenOut, uint256(amount0Delta))
                : (tokenOut < tokenIn, uint256(amount1Delta));
        if (isExactInput) {
            pay(tokenIn, data.payer, msg.sender, amountToPay);
        } else {
            // either initiate the next swap or pay
            if (data.path.hasMultiplePools()) {
                data.path = data.path.skipToken();
                //exactOutputInternal(amountToPay, msg.sender, 0, data);
                revert("system error");
            } else {
                //amountInCached = amountToPay;
                tokenIn = tokenOut; // swap in/out because exact output swaps are reversed
                if(cache.status==1) {
                    pay(tokenIn, data.payer, msg.sender, amountToPay);
                } else if(cache.status==2) {
                    middleToLeft(amountToPay,msg.sender);
                } else if(cache.status==3) {
                    exactOutputFee(amountToPay,msg.sender);
                } else {
                    revert("callback sysem error!");
                }
                
            }
        }
    }

    function exactInputInternal(
        uint256 amountIn,
        address recipient,
        uint160 sqrtPriceLimitX96,
        SwapCallbackData memory data
    ) private returns (uint256 amountOut) {
        // allow swapping to the router address with address 0
        if (recipient == address(0)) recipient = address(this);

        (address tokenIn, address tokenOut, uint24 fee) = data.path.decodeFirstPool();

        bool zeroForOne = tokenIn < tokenOut;

        (int256 amount0, int256 amount1) =
            router.factory().getPool(tokenIn, tokenOut, fee).swap(
                recipient,
                zeroForOne,
                amountIn.toInt256(),
                sqrtPriceLimitX96 == 0
                    ? (zeroForOne ? MIN_SQRT_RATIO + 1 : MAX_SQRT_RATIO - 1)
                    : sqrtPriceLimitX96,
                abi.encode(data)
            );

        return uint256(-(zeroForOne ? amount1 : amount0));
    }

    function pay(
        address token,
        address payer,
        address recipient,
        uint256 value
    ) internal {
        if(token==address(0)){
            require(payer==address(this),"pay eth require this address");
            payable(recipient).transfer(value);
        } else {
            TransferLib.transferFrom(token,payer,recipient,value);
        }
    }

    function getFee(address coin) internal returns(uint amountOut) {
        uint middle=balance(coin,address(this));
        amountOut=middle;
        if(feeAddress!=address(0)&&feeRate>0) {
            uint fee=middle-(middle*(10000-feeRate)/10000);
            if (fee!=0){
                pay(coin,address(this),feeAddress,fee);
                amountOut=amountOut-fee;
                emit Fee(feeAddress,coin,fee);
            }
        }
    }

    function balance(address coin,address user) internal view returns(uint) {
        if(coin==address(0)){
            return user.balance;
        } else {
            return IERC20(coin).balanceOf(user);
        }
    }

    struct ExactOutputCache {
        address coin0;
        uint24 fee0;
        address coin1;
        uint24 fee1;
        address coin2;
        uint24 fee2;
        address coin3;
        uint status;
        uint256 amountInCached;
        address from;
    }

    struct SwapCallbackData {
        bytes path;
        address payer;
    }

    receive() external payable {
    }
}
