// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

enum MessengerProtocol {
    None,
    Allbridge,
    Wormhole,
    LayerZero
}

interface IBridge {
    function receiveTokens(
        uint256 amount,
        bytes32 recipient,
        uint8 destinationChainId,
        bytes32 receiveToken,
        uint256 nonce,
        MessengerProtocol messenger
    ) external;
}

/**
