// File: contracts/ethNftBurn/NFTBurnAccessControl.sol

pragma solidity ^0.8.0;


abstract contract NftBurnAccessControl is AccessControl {
    bytes32 internal constant OWNER_ROLE =0xb19546dff01e856fb3f010c267a7b1c60363cf8a4664e21cc89c26224620214e;



    bool public isPaused;

    modifier whenNotPaused() {
        require(!isPaused, "Wonder Game Character Inventory Burn is paused");
        _;
    }

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(OWNER_ROLE, msg.sender);
    }

    function pause() public onlyRole(OWNER_ROLE) {
        isPaused = true;
    }

    function unpause() public onlyRole(OWNER_ROLE) {
        isPaused = false;
    }
}

