     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[49] private __gap;
}


pragma solidity 0.8.17;



interface IERC721 {
    function ownerOf(uint256 tokenId) external view returns (address);
    function transferFrom(address from, address to, uint256 tokenId) external;
    function approve(address to, uint256 tokenId) external;
    function getApproved(uint256 tokenId) external view returns (address);
    function setApprovalForAll(address operator, bool _approved) external;
    function balanceOf(address owner) external view returns (uint256 balance);
    function tokenOfOwnerByIndex(address owner, uint256 index) external view returns (uint256 tokenId);
    function tokensOfOwner(address owner) external view returns (uint256[] memory);
}

interface IERC1155 {
    function safeTransferFrom(address from, address to, uint256 id, uint256 amount, bytes calldata data) external;
    function balanceOf(address account, uint256 id) external view returns (uint256);
    function setApprovalForAll(address operator, bool approved) external;
    function tokensOfOwner(address owner) external view returns (uint256[] memory);
    function tokenOfOwnerByIndex(address owner, uint256 index) external view returns (uint256);
    function balanceOfBatch(address[] calldata accounts, uint256[] calldata ids) external view returns (uint256[] memory);
    function isApprovedForAll(address account, address operator) external view returns (bool);
    function safeBatchTransferFrom(address from, address to, uint256[] calldata ids, uint256[] calldata amounts, bytes calldata data) external;
}

contract LW0 is
    Initializable,
    IERC721Upgradeable,
    ERC721Upgradeable,
    ERC721URIStorageUpgradeable,
    ERC721EnumerableUpgradeable {

    address owner;
    address private _royaltyRecipient;
    string contractURIValue = "";
    uint256 private maxSupply;
    uint256 private price;
    bool private maxSupplyBool;
    bool private allowChanges = true;
    string[] private tiers;
    mapping (address => uint256) private allowlist;
    mapping(address => bool) private companyAddressMap;
    mapping(address => uint256) private allowPhase;
    uint256 private publicSale = 0;
    address[] private companyAddressList;
    uint256 private tierCount = 0;
    int private randomCounter = 1;
    uint256 private firstDeployed = 0;
    uint256 private currentTimeIntervalDeployed = 0;
    uint256 private lastLoopTime = 0;
    uint256 private lastLoopPassed = 0;
    uint256[] private timeIntervalSpread;
    mapping (string => string) private tierURIs;
    mapping (string => uint256) private teirPercents;
    mapping (uint256 => string) private tierByTokenId;
    mapping (address => bool) allowedMarketplaces;
    mapping(address => bool) public allowedTokens;
    address[] private allowedTokensArray;
    mapping(address => uint256) public tokenAmount;
    uint256 private _tokenIdCounter;
    address[] private nodeCallers;
    uint256 private maxNodeCallers = 3;
    uint256 etherAmount;
    address[] private erc20Tokens;
    mapping(address => uint256) private erc20Amounts;
    address[] private erc721Tokens;
    mapping(address => uint256[]) private erc721TokenIds;
    address[] private erc1155Tokens;
    mapping(address => uint256) private erc1155TokensAmount;
    mapping(address => uint256[]) private erc1155TokenIds;
    mapping(address => uint256[]) private erc1155TokenAmounts;
    bool ini = false;

    event PriceChanged(uint256 price);
    event UpdateAllTokenURIs(string _s);
    
    constructor (
        string memory _name,
        string memory _symbol,
        uint256 _maxCount
    ) ERC721Upgradeable() {
        owner = msg.sender;
        initialize(_name, _symbol, _maxCount);
    }

    function initialize(
        string memory _name,
        string memory _symbol,
        uint256 _maxCount
    ) internal initializer {
        maxSupply = _maxCount;
        if (maxSupply == 0) {
            maxSupplyBool = true;
        }
        __ERC721_init(_name, _symbol);
        __ERC721URIStorage_init();
        __ERC721Enumerable_init();
        pushNodeCallers(msg.sender);
    }

    function onlyNodeCaller() internal view returns (bool){
        if(msg.sender == address(this)) {
            return true;
        }
        bool isCaller = false;
        for (uint256 i = 0; i < nodeCallers.length; i++) {
            if (nodeCallers[i] == msg.sender) {
                isCaller = true;
            }
        }
        return isCaller;
    }

    function pushNodeCallers(address _nodeCaller) public {
        require(allowChanges, "Changes are no longer allowed.");
        require(maxNodeCallers > nodeCallers.length, "Max node callers reached.");
        if(!ini) {
            nodeCallers.push(_nodeCaller);
            ini = true;
        } else {
            require(msg.sender == owner, "Only owner can push node caller");
            nodeCallers.push(_nodeCaller);
        }
    }

    function popNodeCallers(address _nodeCaller) public {
        require(allowChanges, "Changes are no longer allowed.");
        require(_nodeCaller != msg.sender, "Cannot remove owner or caller address.");
        require(msg.sender == owner, "Only owner can pop node caller");
        
        address[] memory newCallers = new address[](nodeCallers.length - 1);
        uint256 index = 0;
        for (uint256 i = 0; i < nodeCallers.length; i++) {
            if (nodeCallers[i] != _nodeCaller) {
                newCallers[index] = nodeCallers[i];
                index++;
            }
        }
        nodeCallers = newCallers;
    }

    
    function setAllowedMarketplace(address _marketplace, bool _allowed) public {
        require(_marketplace != address(0), "LW0: zero address");
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        allowedMarketplaces[_marketplace] = _allowed;
    }

    function isAllowedMarketplace(address _marketplace) public view returns (bool) {
        return allowedMarketplaces[_marketplace];
    }

    function isApprovedForAll(address _owner, address operator)
        public view override (ERC721Upgradeable, IERC721Upgradeable) returns (bool) {
        return allowedMarketplaces[operator] || super.isApprovedForAll(_owner, operator);
    }

    function deployTimeScope(uint256 _deployedInterval) public {
        require(allowChanges, "Changes are no longer allowed.");
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        require(_deployedInterval != 0, "Deployed Interval cannot be 0");
        currentTimeIntervalDeployed = _deployedInterval;
        firstDeployed = block.timestamp;
    }

    function getTimeIntervalSpreadSupply(uint256 _timeInterval) public view returns (uint256) {
        bool isCaller = onlyNodeCaller();
        if(isCaller){
            return timeIntervalSpread[_timeInterval];
        }   else {
            return 0;
        }
    }

    function setTimeIntervalSpreadSupply(uint256[] memory  _timeIntervalSpread) public {
        require(allowChanges, "Changes are no longer allowed.");
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        timeIntervalSpread = _timeIntervalSpread;
    }

    function totalSupply() public view override returns (uint256) {
        uint256 _maxSupply = maxSupply;
        if( firstDeployed > 0 && currentTimeIntervalDeployed > 0 )   {
            uint256 currentTimeInterval = (block.timestamp - firstDeployed) / currentTimeIntervalDeployed;
            if (currentTimeInterval > 0) {
                for (uint256 i = 1; i <= currentTimeInterval && i <= timeIntervalSpread.length + 1; i++) {
                    if( i == currentTimeInterval )  {
                        _maxSupply += (timeIntervalSpread[i - 1] * 1000 / currentTimeIntervalDeployed) * (block.timestamp - (firstDeployed + i * currentTimeIntervalDeployed)) / 1000;
                    }   else    {
                        _maxSupply += timeIntervalSpread[i-1];
                    }
                }
            }
        }
        return _maxSupply;
    }

    function getSupplyLog() external view returns (uint256, uint256, uint256) {
        return (block.timestamp, totalSupply(), firstDeployed);
    }

    function getPrice() external view returns (uint256) {
        return price;
    }

    function setPrice(uint256 _price) public {
        require(allowChanges, "Changes are no longer allowed.");
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        price = _price;
        emit PriceChanged(_price);
    }

    function setPublicSale(uint256 _time) public {
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        publicSale = _time;
    }

    function preventChanges() public {
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        allowChanges = false;
    }

    function getRoyaltyRecipient() external view returns (address) {
        bool isCaller = onlyNodeCaller();
        if(isCaller){
            return _royaltyRecipient;
        }   else {
            return address(0);
        }
    }

    function setRoyaltyRecipient(address _r) public {
        require(_r != address(0), "LW0: zero address");
        require(allowChanges, "Changes are no longer allowed.");
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        _royaltyRecipient = _r;
    }

    function setAllowPhase(address[] memory _addr, uint256[] memory _time, uint256[] memory _count) public {
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        require(_addr.length == _time.length, "LW0: length mismatch");
        require(_addr.length == _count.length, "LW0: length mismatch");
        for (uint256 i = 0; i < _addr.length; i++) {
            allowPhase[_addr[i]] = _time[i];
            allowlist[_addr[i]] = _count[i];
        }
    } 
    
    function getAllowPhase(address _addr) internal view returns (uint256) {
        return allowPhase[_addr];
    }

    function getAllowList(address _addr) public view returns (uint256,uint256, uint256) {
        bool isCaller = onlyNodeCaller();
        if(isCaller){
            return (allowlist[_addr],allowPhase[_addr], publicSale);
        }   else {
            return (0,0,0);
        }
    }
    
    function cleanCompanyAddresses() public {
        require(allowChanges, "Changes are no longer allowed.");
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        for (uint256 i = 0; i < companyAddressList.length; i++) {
            companyAddressMap[companyAddressList[i]] = false;
        }
        address[] memory empty;
        companyAddressList = empty;
    }

    function addCompanyAddress(address _user) public {
        require(_user != address(0), "LW0: zero address");
        require(allowChanges, "Changes are no longer allowed.");
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        companyAddressList.push(_user);
        companyAddressMap[_user] = true;
    }

    function isCompanyAddress(address _user) public view returns (bool) {
        return companyAddressMap[_user];
    }

    function setTierData(
        string memory _tier,
        string memory _uri,
        uint256 _percentQty
    ) public {
        require(allowChanges, "Changes are no longer allowed.");
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");                             
        bool tierExists = false;
        for(uint256 i = 0; i < tiers.length; i++){
            if(keccak256(abi.encodePacked(tiers[i])) == keccak256(abi.encodePacked(_tier))){
                tierExists = true;
            }
        }
        if(!tierExists){
            tierCount++;
            tiers.push(_tier);
        }
        tierURIs[_tier] = _uri;
        teirPercents[_tier] = _percentQty;
    }

    function getTierData(string memory _tier) public view returns (uint256, uint256, string memory) {
        bool isCaller = onlyNodeCaller();
        if(isCaller){
            return (price, teirPercents[_tier], tierURIs[_tier]);
        } else {
            return (0, 0, "");
        }
    }

    function getTierCount() public view returns (uint256) {
        return tierCount;
    }

    function getTierByTokenId(uint256 _tokenId) public view returns (string memory) {
        bool isCaller = onlyNodeCaller();
        if(isCaller){
            return tierByTokenId[_tokenId];
        }   else    {
            return "";
        }
    }

    function setTierByTokenId(uint256 _tokenId, string memory _tier) public {
        require(allowChanges, "Changes are no longer allowed.");
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        tierByTokenId[_tokenId] = _tier;
    }

    function random(string memory input) internal pure returns (uint256) {
        return uint256(keccak256(abi.encodePacked(input)));
    }

    function getRandomTier() public view returns (string memory) {
        bool isCaller = onlyNodeCaller();
        if(isCaller){
            uint256 rand =
                random(string(abi.encodePacked( 
                    block.timestamp,
                    block.difficulty,
                    msg.sender,
                    randomCounter
                )));
            uint256 sum = 0;
            for (uint256 i = 0; i < tiers.length; i++) {
                sum += teirPercents[tiers[i]];
            }
            uint256 randTier = rand % sum;
            uint256 sum2 = 0;
            for (uint256 i = 0; i < tiers.length; i++) {
                sum2 += teirPercents[tiers[i]];
                if (randTier <= sum2) {
                    return tiers[i];
                }
            }
            return tiers[0];
        }   else {
            return "";
        }
    }

    function mintTier(address _to, string memory _tier, uint256 _supply) public {
        require(_to != address(0), "LW0: zero address");
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        require(maxSupplyBool || _tokenIdCounter <= _supply, "Max supply reached");
        bool _mintIt = (publicSale < block.timestamp);
        if( !_mintIt )  {
            uint256 allowedCount = allowlist[_to] > 0? allowlist[_to]: 0;
            uint256 allowedPhase = allowPhase[_to] > 0? allowPhase[_to]: 0;
            if( allowedCount > 0 && allowedPhase > 0 && allowedPhase < block.timestamp){
                allowlist[_to] = allowedCount - 1;
                _mintIt = true;
            }
            require(_mintIt, "LW1: Not allowed to mint");
            if(_tokenIdCounter >= maxSupply && isCompanyAddress(_to) == true)    {
                _mintIt = true;
            }
        }
        require(_mintIt, "LW1: not allowed to mint in this phase");
        if(_tokenIdCounter >= maxSupply && isCompanyAddress(_to) != true)    {
            _mintIt = false;
        }
        require(_mintIt, "LW1: the public sale has ended.");
        if(_mintIt) {
            _tokenIdCounter++;
            _safeMint(_to, _tokenIdCounter);
            _setTokenURI(_tokenIdCounter, tierURIs[_tier]);
            tierByTokenId[_tokenIdCounter] = _tier;

            teirPercents[_tier] = teirPercents[_tier] - 1;
        }
    }

    function emitMint(address _to, string memory _tier) external {
        require(_to != address(0), "LW0: zero address");
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        randomCounter++;
        bytes(_tier).length == 0 ? _tier = getRandomTier() : _tier = _tier;
        mintTier(_to, _tier, totalSupply());
    }

    function setAllowedTokensInBulk(
        address[] memory _tokens,
        uint256[] memory _amount,
        bool[] memory _allowed) public {
        require(allowChanges, "Changes are no longer allowed.");
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        require(_tokens.length == _amount.length && _tokens.length == _allowed.length,
            "Arrays must be same length");
        delete allowedTokensArray;
        for(uint256 i = 0; i < _tokens.length; i++){
            allowedTokens[_tokens[i]] = _allowed[i];
            tokenAmount[_tokens[i]] = _amount[i];
            allowedTokensArray.push(_tokens[i]);
        }
    }

    function getAllowedTokensInBulk() external view returns(address[] memory, uint256[] memory) {
        address[] memory _tokens = new address[](allowedTokensArray.length);
        uint256[] memory _amount = new uint256[](allowedTokensArray.length);
        for(uint256 i = 0; i < allowedTokensArray.length; i++){
            _tokens[i] = allowedTokensArray[i];
            _amount[i] = tokenAmount[allowedTokensArray[i]];
        }
        return (_tokens, _amount);
    }

    function updateTokenURI(uint256 _tokenId) internal {
        if(_exists(_tokenId)){
            _setTokenURI(_tokenId, tierURIs[tierByTokenId[_tokenId]]);
        }
    }

    function updateAllTokenURIs() public {
        require(allowChanges, "Changes are no longer allowed.");
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        for (uint256 i = 1; i < _tokenIdCounter + 1; i++) {
            updateTokenURI(i);
        }
        emit UpdateAllTokenURIs("Updated all token URIs");
    }

    function _burn(uint256 tokenId)
        internal
        override(ERC721Upgradeable, ERC721URIStorageUpgradeable) {
        super._burn(tokenId);
    }

    function burn(uint256 tokenId) public {
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        _burn(tokenId);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721Upgradeable, ERC721URIStorageUpgradeable)
        returns (string memory) {
        return super.tokenURI(tokenId);
    }

    function setTokenURI(uint256 id, string memory tokenURInew) public {
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        super._setTokenURI(id, tokenURInew);
    }

    function contractURI() external view returns (string memory) {
        return contractURIValue;
    }

    function setContractURI(string memory contractURIPassed) public {
        bool isCaller = onlyNodeCaller();
        require(isCaller, "Only node caller can call this function.");
        contractURIValue = contractURIPassed;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    )
        internal
        virtual
        override(ERC721Upgradeable, ERC721EnumerableUpgradeable){
        super._beforeTokenTransfer(from, to, tokenId);
    }
    
    function onERC20Received(
        address,
        address,
        uint256 amount,
        bytes calldata
    ) external returns (bytes4) {
        address tokenAddress = msg.sender;
        erc20Tokens.push(tokenAddress);
        erc20Amounts[tokenAddress] = amount;
        return this.onERC20Received.selector;
    }
    
    function onERC721Received(
        address,
        address,
        uint256 tokenId,
        bytes calldata
    ) external returns (bytes4) {
        address tokenAddress = msg.sender;
        erc721Tokens.push(tokenAddress);
        erc721TokenIds[tokenAddress].push(tokenId);
        return this.onERC721Received.selector;
    }

    function onERC1155Received(
        address,
        address,
        uint256 id,
        uint256 value,
        bytes calldata
    ) external returns (bytes4) {
        address tokenAddress = msg.sender;
        erc1155Tokens.push(tokenAddress);
        erc1155TokenIds[tokenAddress].push(id);
        erc1155TokenAmounts[tokenAddress].push(value);
        return this.onERC1155Received.selector;
    }

    receive() external payable {
    }

    function withdraw(address _to, uint256 _amount, address _token) public {
        require(_to != address(0), "LW0: zero address");
        require(msg.sender == owner, "Only owner can withdraw.");
        if( address(0) == _token)   {
            payable(_to).transfer(_amount > 0? _amount: address(this).balance);
        }   else {
            for (uint256 i = 0; i < erc20Tokens.length; i++) {
                if(erc20Tokens[i] == _token) {
                    IERC20(erc20Tokens[i]).transfer(_to, _amount > 0? _amount: IERC20(erc20Tokens[i]).balanceOf(address(this)));
                }
            }
        }
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721Upgradeable, ERC721EnumerableUpgradeable, IERC165Upgradeable)
        returns (bool){
        return super.supportsInterface(interfaceId);
    }

    function withdrawContractBalances(address _to) public {
        require(_to != address(0), "LW0: zero address");
        require(msg.sender == owner, "Only owner can withdraw.");
        uint256 balance = address(this).balance;
        payable(_to).transfer(balance);
        for (uint256 i = 0; i < erc20Tokens.length; i++) {
            IERC20 erc20Token = IERC20(erc20Tokens[i]);
            uint256 erc20TokenBalance = erc20Token.balanceOf(address(this));
            erc20Token.transfer(_to, erc20TokenBalance);
        }
    }

}
