/**
 * @notice Implements a basic ERC20 staking token with incentive distribution.
 */

contract PulseUnityMarketplace is Ownable, PaymentSplitter {
    event ItemBought(address _buyer, uint _itemId, uint _price);
    event TokensWithdrawn(uint _amount);
    address plsuToken;
    uint rewardedFeesPercentage = 5;

    constructor(
        address _plsuToken,
        address[] memory _holders,
        uint256[] memory _shares
    ) PaymentSplitter(_holders, _shares) {
        plsuToken = _plsuToken;
    }

    /**
     * @dev buy item from marketplace
     * @param _itemId item bought
     * @param _price of the item
     */
    function buyItem(uint _itemId, uint _price) external {
        require(
            IERC20(plsuToken).balanceOf(msg.sender) >= _price,
            "Not enough funds"
        );
        require(
            IERC20(plsuToken).allowance(msg.sender, address(this)) >= _price,
            "Unsufficient allowance"
        );

        IERC20(plsuToken).transferFrom(
            msg.sender,
            owner(),
            (_price * (100 - rewardedFeesPercentage)) / 100
        );

        IERC20(plsuToken).transferFrom(
            msg.sender,
            address(this),
            (_price * (rewardedFeesPercentage)) / 100
        );

        emit ItemBought(msg.sender, _itemId, _price);
    }

    /**
     * @dev change reward fees
     * @param _newFeePercentage of fees
     */
    function changeRewardFees(uint _newFeePercentage) external onlyOwner {
        rewardedFeesPercentage = _newFeePercentage;
    }
}
