contract BaliDancer is ERC721, Ownable, DefaultOperatorFilterer {
    using SafeMath for uint256;
    using Strings for uint256;

    uint256 public constant _TOTALSUPPLY = 10000;
    uint256 public TotalPublicAvailable = 300;
    uint256 public MAX_TOKEN_Available = 400;
    uint256 public maxQuantity = 1;

    uint256 public RARE_TOKEN_NUMBER = 100;
    uint256 public TOKEN_NUMBER = 400;
    uint256 public TOKEN_NUMBER_SECOND = 800;

    uint256 public tokenID = 101;
    uint256 public tokenID_Rare = 1;
    uint256 public _totalMinted = 0;

    uint256 public MAX_PER_WALLET = 10; // maximum amount that user can mint/Wallet
    uint256 public price = 0.3 ether;

    string private rare_baseTokenURI;
    string private second_baseTokenURI;
    string private third_baseTokenURI;

    bool public isPaused = true;
    mapping(address => uint256) public publicClaimedBy;

    constructor() ERC721("Bali Dancer", "BDC") {}

    function setBaseURI(string memory baseURI) public onlyOwner {
        _baseURI = baseURI;
    }

    function setRareBaseURI(string memory baseURI) external onlyOwner {
        rare_baseTokenURI = baseURI;
    }

    function setSecondBaseURI(string memory baseURI) external onlyOwner {
        second_baseTokenURI = baseURI;
    }

    function setThirdBaseURI(string memory baseURI) external onlyOwner {
        third_baseTokenURI = baseURI;
    }

    function change_RARE_TOKEN_NUMBER(uint256 q) external onlyOwner {
        RARE_TOKEN_NUMBER = q;
    }

    function changeTOKEN_NUMBER(uint256 q) external onlyOwner {
        TOKEN_NUMBER = q;
    }

    function changeTOKEN_NUMBER_SECOND(uint256 q) external onlyOwner {
        TOKEN_NUMBER_SECOND = q;
    }

    function setPrice(uint256 _newPrice) public onlyOwner {
        price = _newPrice;
    }

    function setMaxxQtPerTx(uint256 _quantity) public onlyOwner {
        maxQuantity = _quantity;
    }

    function setTotalPublicAvailable(uint256 _publicCollection)
        external
        onlyOwner
    {
        TotalPublicAvailable = _publicCollection;
    }

    function setMAX_TOKEN_Available(uint256 _publicCollection)
        external
        onlyOwner
    {
        MAX_TOKEN_Available = _publicCollection;
    }

    function changeMAX_PER_WALLET(uint256 q) external onlyOwner {
        MAX_PER_WALLET = q;
    }

    modifier isSaleOpen() {
        require(totalSupply() < _TOTALSUPPLY, "Sale end");
        _;
    }

    function flipPauseStatus() public onlyOwner {
        isPaused = !isPaused;
    }

    function getPrice(uint256 _quantity) public view returns (uint256) {
        return _quantity * price;
    }

    function mint(uint256 _tokenID) public payable isSaleOpen {
        require(isPaused == false, "Sale is not active at the moment");
        require(!_exists(_tokenID), "Token Id already exists");
        require(
            totalSupply() + 1 <= _TOTALSUPPLY,
            "Quantity must be lesser then MaxSupply"
        );
        require(
            _tokenID >= 101,
            "Token ID can't be less than minimum token id"
        );
        require(
            _tokenID <= MAX_TOKEN_Available,
            "Token ID can't be greater than maximum token id"
        );
        require(
            totalSupply() + 1 <= TotalPublicAvailable,
            "Token ID can't be greater than Public Available"
        );
        require(msg.value >= 1 * price, "Try to send more ETH");

        publicClaimedBy[msg.sender] += 1;
        require(
            publicClaimedBy[msg.sender] <= MAX_PER_WALLET,
            "Purchase exceeds max allowed"
        );

        _safeMint(msg.sender, _tokenID);
        tokenID++;
        _totalMinted++;
    }

    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        require(
            _exists(tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );

        if (tokenId <= RARE_TOKEN_NUMBER) {
            return
                bytes(rare_baseTokenURI).length > 0
                    ? string(
                        abi.encodePacked(
                            rare_baseTokenURI,
                            "/",
                            tokenId.toString(),
                            ".json"
                        )
                    )
                    : "";
        } else if (tokenId <= TOKEN_NUMBER) {
            string memory base = baseURI();
            return
                bytes(base).length > 0
                    ? string(
                        abi.encodePacked(base, "/", tokenId.toString(), ".json")
                    )
                    : "";
        } else if (tokenId <= TOKEN_NUMBER_SECOND) {
            return
                bytes(second_baseTokenURI).length > 0
                    ? string(
                        abi.encodePacked(
                            second_baseTokenURI,
                            "/",
                            tokenId.toString(),
                            ".json"
                        )
                    )
                    : "";
        } else {
            return
                bytes(third_baseTokenURI).length > 0
                    ? string(
                        abi.encodePacked(
                            third_baseTokenURI,
                            "/",
                            tokenId.toString(),
                            ".json"
                        )
                    )
                    : "";
        }
    }

    function withdraw() public onlyOwner {
        uint256 balance = address(this).balance;
        payable(msg.sender).transfer(balance);
    }

    function airdropFirstPhase(address sendTo, uint256 quantity)
        public
        onlyOwner
    {
        require(totalSupply() + quantity <= _TOTALSUPPLY, "reached max supply");
        require(
            totalSupply() + quantity <= TotalPublicAvailable,
            "Token ID can't be greater than Public Available"
        );
        for (uint256 i = 0; i < quantity; i++) {
            _safeMint(sendTo, tokenID);
            tokenID++;
            _totalMinted++;
        }
    }

    function airdropSingleNFT(address sendTo, uint256 _tokenID)
        public
        onlyOwner
    {
        require(totalSupply() + 1 <= _TOTALSUPPLY, "reached max supply");
        require(
            _tokenID >= 101,
            "Token ID can't be less than minimum token id"
        );
        require(!_exists(_tokenID), "Token Id already exists");
        _safeMint(sendTo, _tokenID);
        tokenID++;
        _totalMinted++;
    }

    function checkIfAlreadyMinted(uint256 _tokenID) public view returns (bool) {
        return _exists(_tokenID);
    }

    function airdropFirst100(address sendTo, uint256 quantity)
        public
        onlyOwner
    {
        require(tokenID_Rare + quantity - 1 <= 100, "reached max supply");
        for (uint256 i = 0; i < quantity; i++) {
            _safeMint(sendTo, tokenID_Rare);
            tokenID_Rare++;
            _totalMinted++;
        }
    }

    function totalSupply() public view returns (uint256) {
        return _totalMinted;
    }

    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public override onlyAllowedOperator(from) {
        super.transferFrom(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public override onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory data
    ) public override onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId, data);
    }
}
