    "contracts/CWFvault.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

/// @title Cryptowalkers Females Vault
/// @author continuumlabs.io
/// @notice This contract stores Cryptowalkers Females minted prior to presale for the purpose of eligible CW holders to claim.

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";

contract CryptowalkersFemalesVault is Ownable, ERC721Holder, ReentrancyGuard {
    using MerkleProof for bytes32[];
    enum State {
        Admin,
        Public
    }
    uint256 public constant VAULT_SUPPLY = 1625;
    uint256 public constant START_INDEX = 1;
    uint256 public constant MAX_CLAIM_AMT = 30;
    IERC721 cryptowalkersFemales;
    State private activeState;
    bytes32 public rootHash;
    mapping(address => bool) public claimed;

    constructor(IERC721 _cryptowalkersFemales) {
        cryptowalkersFemales = _cryptowalkersFemales;
        activeState = State.Admin;
    }

    function setRootHash(bytes32 _newHash) external onlyOwner {
        require(activeState == State.Admin, "State must be set to admin");
        rootHash = _newHash;
    }

    function verifyAddr(
        address _addr,
        uint256 _claims,
        bytes32[] calldata _proof
    ) internal view returns (bool) {
        return
            _proof.verifyCalldata(
                rootHash,
                keccak256(abi.encodePacked(uint256(uint160(_addr)), _claims))
            );
    }

    function claim(uint16 _amt, bytes32[] calldata _proof)
        external
        nonReentrant
    {
        require(_amt <= MAX_CLAIM_AMT, "Exceeds max claim per address");
        require(activeState == State.Public, "Public access inactive");
        require(claimed[msg.sender] == false, "Already claimed");
        require(
            _amt > 0 && verifyAddr(msg.sender, _amt, _proof),
            "Not authorized"
        );
        uint256 vaultBalance = cryptowalkersFemales.balanceOf(address(this));
        require(
            vaultBalance >= _amt,
            "Insuficient balance in Cryptowalkers Females vault"
        );
        claimed[msg.sender] = true;
        uint256 batch = START_INDEX + (VAULT_SUPPLY - vaultBalance);
        uint256 i = batch;
        for (i; i < _amt + batch; ++i) {
            cryptowalkersFemales.transferFrom(address(this), msg.sender, i);
        }
    }

    function getState() external view returns (State) {
        return activeState;
    }

    function setStateToAdmin() external onlyOwner {
        activeState = State.Admin;
    }

